# Function to get spinner frames
get_spinner() {
  local name="$1"
  local var_name="SPINNER_${name}[@]"
  if [[ -n "${!var_name}" ]]; then
    echo "${!var_name}"
    return 0
  else
    return 1
  fi
}

# Function to list all available spinners
list_all_spinners() {
  compgen -A variable | grep "^SPINNER_" | sed 's/SPINNER_//' | sort
}

# Function to check if a spinner exists
spinner_exists() {
  local name="$1"
  local var_name="SPINNER_${name}"
  [[ -n "${!var_name}" ]]
}
